mapper.select:
select

from sys_dict sd
where sd.id = #{id, jdbcType=BIGINT}

    and sd.data_status in

        #{dataStatus,jdbcType=INTEGER}

select

from sys_dict sd


        sd.id = #{queryParam.id, jdbcType=BIGINT}


        and sd.category_code = #{queryParam.categoryCode, jdbcType=VARCHAR}


        and sd.category_name = #{queryParam.categoryName, jdbcType=VARCHAR}


        and sd.dict_name = #{queryParam.dictName, jdbcType=VARCHAR}


        and sd.dict_value = #{queryParam.dictValue, jdbcType=VARCHAR}


        and sd.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sd.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sd.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sd.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}


limit 1

select

from sys_dict sd


        sd.id = #{queryParam.id, jdbcType=BIGINT}


        and sd.category_code = #{queryParam.categoryCode, jdbcType=VARCHAR}


        and sd.category_name = #{queryParam.categoryName, jdbcType=VARCHAR}


        and sd.dict_name = #{queryParam.dictName, jdbcType=VARCHAR}


        and sd.dict_value = #{queryParam.dictValue, jdbcType=VARCHAR}


        and sd.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sd.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sd.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sd.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}

mapper.update:
update sys_dict set data_status = cast('${@org.edu.sagesse.data.base.support.pool.DataStatusPool@DELETE}' as
int)
where id = #{id, jdbcType=BIGINT}
