mapper.select:
select

from sys_hi_login shl
where shl.id = #{id, jdbcType=BIGINT}

select

from sys_hi_login shl


        shl.id = #{queryParam.id, jdbcType=BIGINT}


        and shl.login_ip = #{queryParam.loginIp, jdbcType=VARCHAR}


        and shl.login_account = #{queryParam.loginAccount, jdbcType=VARCHAR}


        and shl.login_equipment = #{queryParam.loginEquipment, jdbcType=VARCHAR}


        and shl.time_consuming = #{queryParam.timeConsuming, jdbcType=BIGINT}


        and shl.error_code = #{queryParam.errorCode, jdbcType=INTEGER}


        and shl.error_message = #{queryParam.errorMessage, jdbcType=VARCHAR}


        and shl.login_result = #{queryParam.loginResult, jdbcType=INTEGER}


        and shl.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


limit 1

