mapper.select:
select

from sys_menu sm
where sm.id = #{id, jdbcType=BIGINT}

    and sm.data_status in

        #{dataStatus,jdbcType=INTEGER}

select

from sys_menu sm


        sm.id = #{queryParam.id, jdbcType=BIGINT}


        and sm.parent_id = #{queryParam.parentId, jdbcType=BIGINT}


        and sm.hierarchical_path = #{queryParam.hierarchicalPath, jdbcType=VARCHAR}


        and sm.menu_code = #{queryParam.menuCode, jdbcType=VARCHAR}


        and sm.menu_title = #{queryParam.menuTitle, jdbcType=VARCHAR}


        and sm.menu_icon = #{queryParam.menuIcon, jdbcType=VARCHAR}


        and sm.visible_status = #{queryParam.visibleStatus, jdbcType=INTEGER}


        and sm.menu_level = #{queryParam.menuLevel, jdbcType=INTEGER}


        and sm.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sm.component = #{queryParam.component, jdbcType=VARCHAR}


        and sm.menu_url = #{queryParam.menuUrl, jdbcType=VARCHAR}


        and sm.remark = #{queryParam.remark, jdbcType=VARCHAR}


        and sm.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sm.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sm.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}


limit 1

select

from sys_menu sm


        sm.id = #{queryParam.id, jdbcType=BIGINT}


        and sm.parent_id = #{queryParam.parentId, jdbcType=BIGINT}


        and sm.hierarchical_path = #{queryParam.hierarchicalPath, jdbcType=VARCHAR}


        and sm.menu_code = #{queryParam.menuCode, jdbcType=VARCHAR}


        and sm.menu_title = #{queryParam.menuTitle, jdbcType=VARCHAR}


        and sm.menu_icon = #{queryParam.menuIcon, jdbcType=VARCHAR}


        and sm.visible_status = #{queryParam.visibleStatus, jdbcType=INTEGER}


        and sm.menu_level = #{queryParam.menuLevel, jdbcType=INTEGER}


        and sm.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sm.component = #{queryParam.component, jdbcType=VARCHAR}


        and sm.menu_url = #{queryParam.menuUrl, jdbcType=VARCHAR}


        and sm.remark = #{queryParam.remark, jdbcType=VARCHAR}


        and sm.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sm.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sm.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}

mapper.update:
update sys_menu set data_status = cast('${@org.edu.sagesse.data.base.support.pool.DataStatusPool@DELETE}' as
int)
where id = #{id, jdbcType=BIGINT}
