mapper.select:
select

from sys_resource sr
where sr.id = #{id, jdbcType=BIGINT}

    and sr.data_status in

        #{dataStatus,jdbcType=INTEGER}

select

from sys_resource sr


        sr.id = #{queryParam.id, jdbcType=BIGINT}


        and sr.category_code = #{queryParam.categoryCode, jdbcType=VARCHAR}


        and sr.category_name = #{queryParam.categoryName, jdbcType=VARCHAR}


        and sr.resource_code = #{queryParam.resourceCode, jdbcType=VARCHAR}


        and sr.resource_name = #{queryParam.resourceName, jdbcType=VARCHAR}


        and sr.request_action_url = #{queryParam.requestActionUrl, jdbcType=VARCHAR}


        and sr.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sr.remark = #{queryParam.remark, jdbcType=VARCHAR}


        and sr.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sr.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sr.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}


limit 1

select

from sys_resource sr


        sr.id = #{queryParam.id, jdbcType=BIGINT}


        and sr.category_code = #{queryParam.categoryCode, jdbcType=VARCHAR}


        and sr.category_name = #{queryParam.categoryName, jdbcType=VARCHAR}


        and sr.resource_code = #{queryParam.resourceCode, jdbcType=VARCHAR}


        and sr.resource_name = #{queryParam.resourceName, jdbcType=VARCHAR}


        and sr.request_action_url = #{queryParam.requestActionUrl, jdbcType=VARCHAR}


        and sr.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sr.remark = #{queryParam.remark, jdbcType=VARCHAR}


        and sr.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sr.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sr.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}

mapper.update:
update sys_resource set data_status = cast('${@org.edu.sagesse.data.base.support.pool.DataStatusPool@DELETE}' as
int)
where id = #{id, jdbcType=BIGINT}
