mapper.select:
select

from sys_role sr
where sr.id = #{id, jdbcType=BIGINT}

    and sr.data_status in

        #{dataStatus,jdbcType=INTEGER}

select

from sys_role sr


        sr.id = #{queryParam.id, jdbcType=BIGINT}


        and sr.role_code = #{queryParam.roleCode, jdbcType=VARCHAR}


        and sr.role_name = #{queryParam.roleName, jdbcType=VARCHAR}


        and sr.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sr.remark = #{queryParam.remark, jdbcType=VARCHAR}


        and sr.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sr.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sr.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}


limit 1

select

from sys_role sr


        sr.id = #{queryParam.id, jdbcType=BIGINT}


        and sr.role_code = #{queryParam.roleCode, jdbcType=VARCHAR}


        and sr.role_name = #{queryParam.roleName, jdbcType=VARCHAR}


        and sr.sort = #{queryParam.sort, jdbcType=INTEGER}


        and sr.remark = #{queryParam.remark, jdbcType=VARCHAR}


        and sr.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and sr.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and sr.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}

mapper.update:
update sys_role set data_status = cast('${@org.edu.sagesse.data.base.support.pool.DataStatusPool@DELETE}' as
int)
where id = #{id, jdbcType=BIGINT}
