mapper.select:
select

from sys_user su
where su.id = #{id, jdbcType=BIGINT}

    and su.data_status in

        #{dataStatus,jdbcType=INTEGER}

select

from sys_user su


        su.id = #{queryParam.id, jdbcType=BIGINT}


        and su.account = #{queryParam.account, jdbcType=VARCHAR}


        and su.password = #{queryParam.password, jdbcType=VARCHAR}


        and su.nickname = #{queryParam.nickname, jdbcType=VARCHAR}


        and su.sex = #{queryParam.sex, jdbcType=INTEGER}


        and su.avatar_url = #{queryParam.avatarUrl, jdbcType=VARCHAR}


        and su.birthday = #{queryParam.birthday, jdbcType=TIMESTAMP}


        and su.user_type = #{queryParam.userType, jdbcType=INTEGER}


        and su.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and su.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and su.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}


limit 1

select

from sys_user su


        su.id = #{queryParam.id, jdbcType=BIGINT}


        and su.account = #{queryParam.account, jdbcType=VARCHAR}


        and su.password = #{queryParam.password, jdbcType=VARCHAR}


        and su.nickname = #{queryParam.nickname, jdbcType=VARCHAR}


        and su.sex = #{queryParam.sex, jdbcType=INTEGER}


        and su.avatar_url = #{queryParam.avatarUrl, jdbcType=VARCHAR}


        and su.birthday = #{queryParam.birthday, jdbcType=TIMESTAMP}


        and su.user_type = #{queryParam.userType, jdbcType=INTEGER}


        and su.data_status = #{queryParam.dataStatus, jdbcType=INTEGER}


        and su.gmt_create = #{queryParam.gmtCreate, jdbcType=TIMESTAMP}


        and su.gmt_modify = #{queryParam.gmtModify, jdbcType=TIMESTAMP}

mapper.update:
update sys_user set data_status = cast('${@org.edu.sagesse.data.base.support.pool.DataStatusPool@DELETE}' as
int)
where id = #{id, jdbcType=BIGINT}
